const EFFECT_STYLE = {
  Gain:            { bg:"#3a2a1a", text:"#f0a855" },
  Fuzz:            { bg:"#2a1a1a", text:"#e06b5a" },
  Compression:     { bg:"#1a2a2a", text:"#5ac8a0" },
  Modulation:      { bg:"#1e1a2a", text:"#9b7ee8" },
  Delay:           { bg:"#2a2218", text:"#e8a84d" },
  Reverb:          { bg:"#182a2a", text:"#4dd4e8" },
  "Multi Effects": { bg:"#2a1a22", text:"#e84da0" },
  Utility:         { bg:"#222222", text:"#a0a0a0" },
  Preamp:          { bg:"#1a2a1e", text:"#6dd85c" },
  "Amp/Cab Sim":   { bg:"#2a2418", text:"#d4b64d" },
  Other:           { bg:"#222222", text:"#888888" }
};

const MANUFACTURERS = ["All",...new Set(DATA.map(d=>d.manufacturer))];
const EFFECT_TYPES  = ["All",...new Set(DATA.map(d=>d.effect_type))];

const MFG_STATS = {};
DATA.forEach(p => {
  if(!MFG_STATS[p.manufacturer]) MFG_STATS[p.manufacturer]={total:0,active:0};
  MFG_STATS[p.manufacturer].total++;
  if(p.in_production) MFG_STATS[p.manufacturer].active++;
});

export default function PedalDatabase(){
  const [search,setSearch]=useState("");
  const [mfgFilter,setMfgFilter]=useState("All");
  const [effectFilter,setEffectFilter]=useState("All");
  const [prodFilter,setProdFilter]=useState("All");
  const [sortCol,setSortCol]=useState("manufacturer");
  const [sortDir,setSortDir]=useState(1);
  const [expandedId,setExpandedId]=useState(null);

  const filtered=useMemo(()=>{
    let d=DATA;
    if(search){const s=search.toLowerCase();d=d.filter(p=>p.model.toLowerCase().includes(s)||p.manufacturer.toLowerCase().includes(s));}
    if(mfgFilter!=="All") d=d.filter(p=>p.manufacturer===mfgFilter);
    if(effectFilter!=="All") d=d.filter(p=>p.effect_type===effectFilter);
    if(prodFilter==="In Production") d=d.filter(p=>p.in_production===1);
    if(prodFilter==="Discontinued") d=d.filter(p=>p.in_production===0);
    return d.sort((a,b)=>{
      let va=a[sortCol],vb=b[sortCol];
      if(va==null&&vb==null)return 0;if(va==null)return 1;if(vb==null)return -1;
      if(typeof va==="string")return va.localeCompare(vb)*sortDir;
      return(va-vb)*sortDir;
    });
  },[search,mfgFilter,effectFilter,prodFilter,sortCol,sortDir]);

  const handleSort=col=>{if(sortCol===col)setSortDir(d=>d*-1);else{setSortCol(col);setSortDir(1);}};
  const SortIcon=({col})=>{
    if(sortCol!==col)return <span style={{color:"#3d3d3d",marginLeft:3,fontSize:9}}>⇅</span>;
    return <span style={{color:"#f0a855",marginLeft:3,fontSize:9}}>{sortDir===1?"▲":"▼"}</span>;
  };
  const fmt=c=>c?`$${(c/100).toFixed(0)}`:"—";
  const Null=()=><span style={{color:"#3a3a3a",fontStyle:"italic"}}>—</span>;
  const val=v=>v!=null?v:<Null/>;

  const COLS=[
    {key:"id",label:"#",w:40,align:"center"},
    {key:"manufacturer",label:"Manufacturer",w:140},
    {key:"model",label:"Model",w:175},
    {key:"effect_type",label:"Effect",w:108},
    {key:"in_production",label:"Status",w:80,align:"center"},
    {key:"inputs",label:"Inputs",w:130},
    {key:"outputs",label:"Outputs",w:150},
    {key:"power_voltage",label:"Voltage",w:88},
    {key:"power_current_ma",label:"mA",w:52,align:"right"},
    {key:"dimensions",label:"Dimensions",w:130},
    {key:"msrp_cents",label:"MSRP",w:64,align:"right"},
    {key:"data_reliability",label:"Rel",w:52,align:"center"}
  ];

  return(
    <div style={{fontFamily:"'SF Mono','Fira Code','Consolas',monospace",background:"#111",color:"#c8c8c8",minHeight:"100vh",padding:"24px 20px 40px"}}>
      {/* HEADER */}
      <div style={{display:"flex",alignItems:"flex-end",justifyContent:"space-between",marginBottom:18,flexWrap:"wrap",gap:8}}>
        <div>
          <div style={{display:"flex",alignItems:"baseline",gap:12,flexWrap:"wrap"}}>
            <h1 style={{margin:0,fontSize:24,fontWeight:700,color:"#f0f0f0",letterSpacing:"-0.5px",fontFamily:"'Helvetica Neue',sans-serif"}}>Pedal Database</h1>
            <span style={{fontSize:10,color:"#3d3d3d",fontFamily:"monospace"}}>104 pedals · 8 manufacturers</span>
          </div>
        </div>
      </div>

      {/* MFG PILLS */}
      <div style={{display:"flex",gap:6,flexWrap:"wrap",marginBottom:14}}>
        {MANUFACTURERS.slice(1).map(m=>{
          const s=MFG_STATS[m];const active=mfgFilter===m;
          return(<button key={m} onClick={()=>setMfgFilter(active?"All":m)} style={{
            padding:"5px 10px",borderRadius:4,border:active?"1px solid #f0a855":"1px solid #2a2a2a",
            background:active?"rgba(240,168,85,0.12)":"#1a1a1a",
            color:active?"#f0a855":"#5a5a5a",fontSize:11,cursor:"pointer",fontFamily:"monospace",transition:"all 0.15s"
          }}>{m} <span style={{color:active?"rgba(240,168,85,0.5)":"#333"}}>({s.active}/{s.total})</span></button>);
        })}
      </div>

      {/* FILTER BAR */}
      <div style={{display:"flex",gap:10,flexWrap:"wrap",marginBottom:16,alignItems:"center"}}>
        <div style={{position:"relative",flex:"1 1 220px",maxWidth:300}}>
          <span style={{position:"absolute",left:10,top:"50%",transform:"translateY(-50%)",color:"#3d3d3d",fontSize:13,pointerEvents:"none"}}>⌕</span>
          <input value={search} onChange={e=>setSearch(e.target.value)} placeholder="Search model or manufacturer…" style={{
            width:"100%",boxSizing:"border-box",padding:"7px 12px 7px 28px",borderRadius:5,
            border:"1px solid #2a2a2a",background:"#161616",color:"#e0e0e0",fontSize:12,outline:"none",fontFamily:"monospace"
          }}/>
        </div>
        <select value={effectFilter} onChange={e=>setEffectFilter(e.target.value)} style={{
          padding:"7px 10px",borderRadius:5,border:"1px solid #2a2a2a",background:"#161616",
          color:"#a0a0a0",fontSize:12,outline:"none",fontFamily:"monospace",minWidth:140
        }}>
          {EFFECT_TYPES.map(o=><option key={o} value={o}>{o==="All"?"Effect: All":o}</option>)}
        </select>
        <select value={prodFilter} onChange={e=>setProdFilter(e.target.value)} style={{
          padding:"7px 10px",borderRadius:5,border:"1px solid #2a2a2a",background:"#161616",
          color:"#a0a0a0",fontSize:12,outline:"none",fontFamily:"monospace",minWidth:140
        }}>
          {["All","In Production","Discontinued"].map(o=><option key={o} value={o}>{o==="All"?"Status: All":o}</option>)}
        </select>
        <span style={{marginLeft:"auto",fontSize:10,color:"#3d3d3d"}}>{filtered.length} pedal{filtered.length!==1?"s":""} shown</span>
      </div>

      {/* TABLE */}
      <div style={{overflowX:"auto",borderRadius:6,border:"1px solid #222",boxShadow:"0 4px 24px rgba(0,0,0,0.4)"}}>
        <table style={{borderCollapse:"collapse",width:"100%",minWidth:1050}}>
          <thead>
            <tr>
              {COLS.map(c=>(
                <th key={c.key} onClick={()=>handleSort(c.key)} style={{
                  padding:"9px 10px",background:"#151515",color:"#4a4a4a",fontSize:10,fontWeight:600,
                  textTransform:"uppercase",letterSpacing:"0.08em",whiteSpace:"nowrap",cursor:"pointer",
                  userSelect:"none",borderBottom:"1px solid #222",textAlign:c.align||"left",
                  width:c.w,position:"sticky",top:0,zIndex:2
                }}>{c.label}<SortIcon col={c.key}/></th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filtered.map((p,i)=>{
              const isExp=expandedId===p.id;
              const es=EFFECT_STYLE[p.effect_type]||EFFECT_STYLE.Other;
              const rowBg=isExp?"#1c1c1c":(i%2===0?"#141414":"#181818");
              return[
                <tr key={p.id} onClick={()=>setExpandedId(isExp?null:p.id)}
                  style={{background:rowBg,cursor:"pointer",transition:"background 0.12s"}}
                  onMouseEnter={e=>{if(!isExp)e.currentTarget.style.background="#1e1e1e";}}
                  onMouseLeave={e=>{if(!isExp)e.currentTarget.style.background=rowBg;}}>
                  <td style={{padding:"7px 10px",textAlign:"center",color:"#3a3a3a",fontSize:10,borderBottom:"1px solid #1e1e1e"}}>{p.id}</td>
                  <td style={{padding:"7px 10px",color:"#d0d0d0",fontSize:12,fontWeight:600,borderBottom:"1px solid #1e1e1e",fontFamily:"'Helvetica Neue',sans-serif"}}>{p.manufacturer}</td>
                  <td style={{padding:"7px 10px",color:"#f0f0f0",fontSize:12.5,fontWeight:500,borderBottom:"1px solid #1e1e1e",fontFamily:"'Helvetica Neue',sans-serif"}}>{p.model}</td>
                  <td style={{padding:"7px 10px",borderBottom:"1px solid #1e1e1e"}}>
                    <span style={{display:"inline-block",padding:"3px 8px",borderRadius:3,fontSize:10.5,fontWeight:600,
                      background:es.bg,color:es.text,border:`1px solid ${es.text}33`}}>{p.effect_type}</span>
                  </td>
                  <td style={{padding:"7px 10px",textAlign:"center",borderBottom:"1px solid #1e1e1e"}}>
                    <span style={{display:"inline-flex",alignItems:"center",gap:5,fontSize:10.5,fontWeight:600}}>
                      <span style={{width:7,height:7,borderRadius:"50%",
                        background:p.in_production?"#4caf50":"#444",
                        boxShadow:p.in_production?"0 0 6px #4caf5066":"none"}}/>
                      <span style={{color:p.in_production?"#7dd88d":"#555"}}>{p.in_production?"Active":"Disc."}</span>
                    </span>
                  </td>
                  <td style={{padding:"7px 10px",color:"#6a6a6a",fontSize:11,borderBottom:"1px solid #1e1e1e"}}>{val(p.inputs)}</td>
                  <td style={{padding:"7px 10px",color:"#6a6a6a",fontSize:11,borderBottom:"1px solid #1e1e1e"}}>{val(p.outputs)}</td>
                  <td style={{padding:"7px 10px",color:"#6a6a6a",fontSize:11,borderBottom:"1px solid #1e1e1e"}}>{val(p.power_voltage)}</td>
                  <td style={{padding:"7px 10px",textAlign:"right",color:"#6a6a6a",fontSize:11,borderBottom:"1px solid #1e1e1e"}}>{val(p.power_current_ma)}</td>
                  <td style={{padding:"7px 10px",color:"#6a6a6a",fontSize:11,borderBottom:"1px solid #1e1e1e",whiteSpace:"nowrap"}}>{val(p.dimensions)}</td>
                  <td style={{padding:"7px 10px",textAlign:"right",color:p.msrp_cents?"#f0a855":"#3a3a3a",fontSize:12,fontWeight:600,borderBottom:"1px solid #1e1e1e"}}>{fmt(p.msrp_cents)}</td>
                  <td style={{padding:"7px 10px",textAlign:"center",borderBottom:"1px solid #1e1e1e"}}>
                    <span style={{display:"inline-block",fontSize:9,fontWeight:700,letterSpacing:"0.04em",padding:"2px 5px",borderRadius:2,
                      background:p.data_reliability==="High"?"#1a3322":p.data_reliability==="Medium"?"#2a2518":"#2a1818",
                      color:p.data_reliability==="High"?"#5ccc88":p.data_reliability==="Medium"?"#c8a840":"#cc6060",
                      border:`1px solid ${p.data_reliability==="High"?"#5ccc8833":p.data_reliability==="Medium"?"#c8a84033":"#cc606033"}`
                    }}>{p.data_reliability}</span>
                  </td>
                </tr>,
                isExp&&(
                  <tr key={`exp-${p.id}`}>
                    <td colSpan={12} style={{background:"#1a1a1a",borderBottom:"1px solid #2a2a2a",padding:"16px 18px 18px"}}>
                      <div style={{display:"grid",gridTemplateColumns:"repeat(auto-fill,minmax(160px,1fr))",gap:"12px 20px"}}>
                        {[["Plug Size",p.power_plug_size],["Polarity",p.power_polarity],["Color Options",p.color_options]].map(([label,value])=>(
                          <div key={label}>
                            <div style={{color:"#3d3d3d",fontSize:9,textTransform:"uppercase",letterSpacing:"0.1em",marginBottom:3}}>{label}</div>
                            <div style={{color:"#b0b0b0",fontSize:11.5}}>{value??<Null/>}</div>
                          </div>
                        ))}
                        <div>
                          <div style={{color:"#3d3d3d",fontSize:9,textTransform:"uppercase",letterSpacing:"0.1em",marginBottom:3}}>Product Page</div>
                          {p.product_page
                            ?<a href={p.product_page} target="_blank" rel="noopener noreferrer"
                              style={{color:"#f0a855",fontSize:11,textDecoration:"none"}}
                              onMouseEnter={e=>e.target.style.textDecoration="underline"}
                              onMouseLeave={e=>e.target.style.textDecoration="none"}>Open ↗</a>
                            :<Null/>}
                        </div>
                        <div>
                          <div style={{color:"#3d3d3d",fontSize:9,textTransform:"uppercase",letterSpacing:"0.1em",marginBottom:3}}>Manual</div>
                          {p.instruction_manual
                            ?<a href={p.instruction_manual} target="_blank" rel="noopener noreferrer"
                              style={{color:"#f0a855",fontSize:11,textDecoration:"none"}}
                              onMouseEnter={e=>e.target.style.textDecoration="underline"}
                              onMouseLeave={e=>e.target.style.textDecoration="none"}>Open ↗</a>
                            :<Null/>}
                        </div>
                      </div>
                    </td>
                  </tr>
                )
              ];
            })}
          </tbody>
        </table>
        {filtered.length===0&&(
          <div style={{padding:48,textAlign:"center",color:"#3d3d3d",fontSize:13}}>No pedals match your filters.</div>
        )}
      </div>
    </div>
  );
}
